import React from 'react';

export default function Installment({ data }) {
  const { id, value, difference, percentage, profit } = data;
  const classPositiveValue = 'green-text';
  const classPositivePercent = 'green-text';
  const classNegativeValue = 'red-text';
  const classNegativePercent = 'red-text';

  const classValue = profit ? classPositiveValue : classNegativeValue;
  const classPercent = profit ? classPositivePercent : classNegativePercent;
  return (
    <div className="col s6 m3 l2">
      <div style={styles.flexRow}>
        <span>{id}</span>
        <div>
          <div className={classValue}>{value}</div>
          <div>{difference}</div>
          <div>{percentage}</div>
        </div>
      </div>
    </div>
  );
}

const styles = {
  flexRow: {
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'flexStart',

    border: '1px solid black',
    borderRadius: '4px',
    padding: '4px',
    margin: '4px',
  },
};
